mapper.select:
select count(1) from (select c.id,c.name,c.birthday,c.sex,c.idcard,c.phone,c.province,c.city,c.address,c.email, c.state,c.use_state as useState,oc.realname as createOperator,c.create_time as createTime, ou.realname as updateOperator,c.update_time as updateTime,c.remark from t_customer c left outer join t_operator oc on c.create_id=oc.id left outer join t_operator ou on c.update_id=ou.id
c.name like CONCAT('%',#{name},'%')
and c.phone like CONCAT('%',#{phone},'%')
and c.idcard like CONCAT('%',#{idcard},'%')
and c.state=#{state}
and c.use_state=#{useState}
) a

mapper.update:
update t_customer
set state = CASE state WHEN 0 THEN 1 ELSE 0 END 
where id = #{id,jdbcType=INTEGER}

update t_customer
set use_State = CASE use_State WHEN 0 THEN 1 ELSE 0 END 
where id = #{id,jdbcType=INTEGER}

mapper.select:
select c.id,c.name,c.birthday,c.sex,c.idcard,c.phone,c.province,c.city,c.address,c.email, c.state,c.use_state as useState,oc.realname as createOperator,c.create_time as createTime, ou.realname as updateOperator,c.update_time as updateTime,c.remark from t_customer c left outer join t_operator oc on c.create_id=oc.id left outer join t_operator ou on c.update_id=ou.id
c.name like CONCAT('%',#{name},'%')
and c.phone like CONCAT('%',#{phone},'%')
and c.idcard like CONCAT('%',#{idcard},'%')
and c.state=#{state}
and c.use_state=#{useState}
order by c.id limit #{begin,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
