mapper.select:
select count(1) from (select s.id,s.name,s.contacts,s.phone,s.province,s.city,s.address, s.state,s.use_state as useState,oc.realname as createOperator,s.create_time as createTime, ou.realname as updateOperator,s.update_time as updateTime,s.remark from t_supplier s left outer join t_operator oc on s.create_id=oc.id left outer join t_operator ou on s.update_id=ou.id
s.name like CONCAT('%',#{name},'%')
and s.contacts like CONCAT('%',#{contacts},'%')
and s.phone like CONCAT('%',#{phone},'%')
and s.state=#{state}
and s.use_state=#{useState}
) a

mapper.update:
update t_supplier
set state = CASE state WHEN 0 THEN 1 ELSE 0 END 
where id = #{id,jdbcType=INTEGER}

update t_supplier
set use_State = CASE use_State WHEN 0 THEN 1 ELSE 0 END 
where id = #{id,jdbcType=INTEGER}

mapper.select:
select s.id,s.name,s.contacts,s.phone,s.province,s.city,s.address, s.state,s.use_state as useState,oc.realname as createOperator,s.create_time as createTime, ou.realname as updateOperator,s.update_time as updateTime,s.remark from t_supplier s left outer join t_operator oc on s.create_id=oc.id left outer join t_operator ou on s.update_id=ou.id
s.name like CONCAT('%',#{name},'%')
and s.contacts like CONCAT('%',#{contacts},'%')
and s.phone like CONCAT('%',#{phone},'%')
and s.state=#{state}
and s.use_state=#{useState}
order by s.id limit #{begin,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
